mapper.select:
SELECT u.id, p.protectboard_userno , p.protectboardno , p.protectboard_title , p.protectboard_content , p.breeds , p.protectboard_ani_category , p.protectboard_reg_date  , p.protectstatus , p.starttime , p.deadline ,  p.fileAttached
FROM users u, protectboard p
WHERE u.userno = p.protectboard_userno AND p.protectboardno = #{protectboardno}

mapper.update:
UPDATE protectboard
SET protectboard_title = #{protectboard_title} , protectboard_content = #{protectboard_content} ,  breeds = #{breeds} , protectboard_ani_category = #{protectboard_ani_category} , protectstatus = #{protectstatus} , starttime = #{starttime} , deadline = #{deadline} , fileAttached = #{fileAttached}
WHERE protectboard_userno = #{protectboard_userno} and protectboardno = #{protectboardno}

UPDATE protectboard
SET fileAttached = #{fileAttached}
WHERE protectboardno = #{protectboardno}
